import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { collection, query, orderBy, limit, getDocs, where } from 'firebase/firestore';
import { db } from '../firebase/config';

function HomePage() {
  const [isHovered, setIsHovered] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const [topPromoters, setTopPromoters] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  
  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 20);
    };
    
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Получаем данные промоутеров из Firebase
  useEffect(() => {
    const fetchPromoters = async () => {
      try {
        console.log('Начинаем загрузку промоутеров...');
        const promotersRef = collection(db, 'promoters');
        
        // Создаем запрос для получения промоутеров, отсортированных по количеству листовок
        const q = query(
          promotersRef,
          orderBy('leafletsCount', 'desc'),
          limit(3)
        );
        
        console.log('Выполняем запрос к Firebase...');
        const querySnapshot = await getDocs(q);
        
        console.log('Получены данные. Количество документов:', querySnapshot.size);
        
        const promotersData = querySnapshot.docs.map(doc => {
          const data = doc.data();
          console.log('Данные промоутера:', data);
          return {
            id: doc.id,
            name: data.name || 'Без имени',
            avatar: data.avatarUrl || data.avatar,
            flyers: data.leafletsCount || 0,
            rating: data.rating || 5,
            speed: data.speed || `${data.leafletsCount || 0} листовок/час`,
            area: data.area || 'Не указан',
            achievements: data.achievements || []
          };
        });
        
        console.log('Обработанные данные промоутеров:', promotersData);
        setTopPromoters(promotersData);
        setLoading(false);
      } catch (err) {
        console.error('Ошибка при загрузке промоутеров:', err);
        setError('Не удалось загрузить данные промоутеров: ' + err.message);
        setLoading(false);
      }
    };

    fetchPromoters();
  }, []);

  // Компонент загрузки
  const LoadingCard = () => (
    <div className="bg-dark/50 backdrop-blur-xl p-6 rounded-xl animate-pulse">
      <div className="flex items-center gap-4">
        <div className="w-14 h-14 rounded-full bg-primary/20"></div>
        <div className="flex-1">
          <div className="h-4 w-24 bg-primary/20 rounded mb-2"></div>
          <div className="h-3 w-32 bg-primary/10 rounded"></div>
        </div>
      </div>
    </div>
  );

  // Компонент ошибки
  const ErrorMessage = () => (
    <div className="text-center p-4 rounded-xl bg-red-500/10 text-red-500">
      <i className="fas fa-exclamation-circle mr-2"></i>
      {error}
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-b from-dark-darker to-dark">
      <div className="relative">
        {/* Декоративный фоновый паттерн */}
        <div className="absolute inset-0 overflow-hidden">
          <div className="absolute top-0 left-0 w-full h-[400px] bg-gradient-to-b from-primary/5 to-transparent"></div>
          <div className="absolute top-0 left-1/4 w-96 h-96 bg-primary/10 rounded-full blur-3xl animate-pulse"></div>
          <div className="absolute top-0 right-1/4 w-96 h-96 bg-secondary/10 rounded-full blur-3xl animate-pulse delay-1000"></div>
          <div className="grid grid-cols-8 gap-4 absolute inset-0 opacity-10">
            {Array.from({ length: 64 }).map((_, i) => (
              <div key={i} className="w-full h-4 bg-gradient-to-r from-primary/20 to-secondary/20 rounded-full transform rotate-45"></div>
            ))}
          </div>
        </div>

        <header className={`sticky top-0 z-50 transition-all duration-300 ${scrolled ? 'bg-dark-darker/80' : 'bg-transparent'}`}>
          {/* Светящаяся линия */}
          <div className="absolute bottom-0 left-0 w-full h-px bg-gradient-to-r from-transparent via-primary to-transparent opacity-50"></div>
          
          <div className={`relative transition-all duration-300 ${scrolled ? 'py-2' : 'py-4'}`}>
            <div className="max-w-7xl mx-auto px-4">
              <div className="flex flex-col md:flex-row justify-between items-center gap-4">
                {/* Логотип */}
                <Link to="/" className="text-4xl font-bold relative group z-10">
                  <span className="bg-gradient-to-r from-primary to-secondary text-transparent bg-clip-text">
                    Промо
                  </span>
                  <span className="text-white">Рейтинг</span>
                  <div className="absolute -bottom-2 left-0 w-0 h-0.5 bg-gradient-to-r from-primary to-secondary group-hover:w-full transition-all duration-300"></div>
                </Link>
                
                {/* Навигация */}
                <nav className="flex flex-col md:flex-row items-center gap-6">
                  <Link to="/" className="relative text-white font-medium group">
                    <span>Главная</span>
                    <div className="absolute -bottom-1 left-0 w-0 h-0.5 bg-primary group-hover:w-full transition-all duration-300"></div>
                  </Link>
                  <Link to="/rating" className="relative text-white font-medium group">
                    <span>Рейтинг</span>
                    <div className="absolute -bottom-1 left-0 w-0 h-0.5 bg-primary group-hover:w-full transition-all duration-300"></div>
                  </Link>
                  <Link to="/login" className="relative overflow-hidden px-6 py-2 bg-gradient-to-r from-primary to-secondary rounded-lg font-medium text-white transition-all duration-300 hover:shadow-neon group">
                    <span className="relative z-10">Вход</span>
                    <div className="absolute inset-0 bg-gradient-to-r from-secondary to-primary opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
                  </Link>
                </nav>
              </div>
            </div>
          </div>
        </header>

        {/* Добавляем приветственную секцию под хедером */}
        <div className="relative">
          <div className="max-w-7xl mx-auto px-4 py-16 md:py-24">
            <div className="grid md:grid-cols-2 gap-12 items-center">
              <div className="relative z-10">
                <h2 className="text-4xl md:text-5xl font-bold mb-6">
                  <span className="bg-gradient-to-r from-primary to-secondary text-transparent bg-clip-text">
                    Лучшие промоутеры
                  </span>
                  <br />
                  <span className="text-white">Петрозаводска</span>
                </h2>
                <p className="text-gray-300 text-lg mb-8">
                  Мы собрали самых эффективных и надежных промоутеров города. 
                  Отслеживайте их достижения и выбирайте лучших для вашей рекламной кампании.
                </p>
                <div className="flex flex-wrap gap-4">
                  <button className="px-8 py-3 bg-gradient-to-r from-primary to-secondary rounded-lg font-medium text-white transition-all duration-300 hover:shadow-neon transform hover:-translate-y-1">
                    Начать сейчас
                  </button>
                  <button className="px-8 py-3 bg-white/10 rounded-lg font-medium text-white transition-all duration-300 hover:bg-white/20 transform hover:-translate-y-1">
                    Узнать больше
                  </button>
                </div>
                
                {/* Статистика */}
                <div className="grid grid-cols-3 gap-4 mt-12">
                  <div className="text-center">
                    <div className="text-3xl font-bold bg-gradient-to-r from-primary to-secondary text-transparent bg-clip-text">
                      150+
                    </div>
                    <div className="text-gray-400 text-sm">Промоутеров</div>
                  </div>
                  <div className="text-center">
                    <div className="text-3xl font-bold bg-gradient-to-r from-primary to-secondary text-transparent bg-clip-text">
                      10k+
                    </div>
                    <div className="text-gray-400 text-sm">Листовок в день</div>
                  </div>
                  <div className="text-center">
                    <div className="text-3xl font-bold bg-gradient-to-r from-primary to-secondary text-transparent bg-clip-text">
                      98%
                    </div>
                    <div className="text-gray-400 text-sm">Довольных клиентов</div>
                  </div>
                </div>
              </div>
              
              {/* Декоративная карточка */}
              <div className="relative">
                <div className="absolute -inset-4 bg-gradient-to-r from-primary to-secondary rounded-2xl blur-2xl opacity-20 animate-pulse"></div>
                <div className="relative bg-dark/50 backdrop-blur-xl p-8 rounded-2xl border border-white/10">
                  <div className="flex items-center gap-4 mb-6">
                    <div className="w-12 h-12 rounded-xl bg-gradient-to-r from-primary to-secondary flex items-center justify-center">
                      <i className="fas fa-chart-line text-2xl text-white"></i>
                    </div>
                    <div>
                      <h3 className="text-xl font-semibold text-white">Рейтинг растет</h3>
                      <p className="text-gray-400">Последнее обновление 5 мин назад</p>
                    </div>
                  </div>
                  
                  <div className="space-y-4">
                    {[...Array(3)].map((_, i) => (
                      <div key={i} className="flex items-center gap-3">
                        <div className="w-2 h-2 rounded-full bg-primary"></div>
                        <div className="flex-1 h-2 bg-white/5 rounded-full overflow-hidden">
                          <div 
                            className="h-full bg-gradient-to-r from-primary to-secondary rounded-full" 
                            style={{ width: `${90 - i * 15}%` }}
                          ></div>
                        </div>
                        <span className="text-sm text-gray-400">{90 - i * 15}%</span>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Остальной контент */}
      <main className="max-w-7xl mx-auto px-4 py-12">
        <section className="mb-16 relative">
          {/* Декоративный фоновый элемент */}
          <div className="absolute -top-10 left-1/2 -translate-x-1/2 w-96 h-96 bg-primary/20 rounded-full blur-3xl"></div>
          
          <h1 className="relative text-5xl font-bold text-center mb-12">
            <span className="bg-gradient-to-r from-primary to-secondary text-transparent bg-clip-text">
              🏆 Самый быстрый промоутер месяца 🏆
            </span>
          </h1>
          
          {loading ? (
            <LoadingCard />
          ) : error ? (
            <ErrorMessage />
          ) : topPromoters.length > 0 ? (
            <div 
              className="relative bg-dark/50 backdrop-blur-xl p-8 rounded-2xl shadow-2xl hover:shadow-neon transition-all duration-500 transform hover:-translate-y-2"
              onMouseEnter={() => setIsHovered(true)}
              onMouseLeave={() => setIsHovered(false)}
            >
              <div className="absolute inset-0 bg-gradient-to-r from-primary/10 to-secondary/10 rounded-2xl"></div>
              
              <div className="relative flex flex-col md:flex-row items-center gap-8">
                <div className="relative group">
                  <div className="absolute -inset-1 bg-gradient-to-r from-primary to-secondary rounded-full blur opacity-25 group-hover:opacity-75 transition duration-500"></div>
                  <img 
                    src={topPromoters[0].avatar || "https://via.placeholder.com/60"} 
                    alt={topPromoters[0].name} 
                    className="relative w-24 h-24 md:w-32 md:h-32 rounded-full object-cover ring-4 ring-primary/50 transform group-hover:scale-105 transition duration-500"
                  />
                </div>
                
                <div className="flex-1 text-center md:text-left">
                  <h3 className="text-2xl md:text-3xl font-bold text-white mb-4">{topPromoters[0].name}</h3>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div className="space-y-4">
                      <div className="flex items-center gap-3 text-gray-300">
                        <span className="flex items-center justify-center w-10 h-10 rounded-lg bg-primary/20">
                          <i className="fas fa-file-alt text-primary"></i>
                        </span>
                        <div>
                          <p className="text-sm text-gray-400">Всего листовок</p>
                          <p className="font-semibold">{topPromoters[0].flyers} штук</p>
                        </div>
                      </div>
                      
                      <div className="flex items-center gap-3 text-gray-300">
                        <span className="flex items-center justify-center w-10 h-10 rounded-lg bg-yellow-500/20">
                          <i className="fas fa-star text-yellow-500"></i>
                        </span>
                        <div>
                          <p className="text-sm text-gray-400">Рейтинг</p>
                          <p className="font-semibold">{topPromoters[0].rating} из 5</p>
                        </div>
                      </div>
                    </div>
                    
                    <div className="space-y-4">
                      <div className="flex items-center gap-3 text-gray-300">
                        <span className="flex items-center justify-center w-10 h-10 rounded-lg bg-secondary/20">
                          <i className="fas fa-bolt text-secondary"></i>
                        </span>
                        <div>
                          <p className="text-sm text-gray-400">Скорость</p>
                          <p className="font-semibold">{topPromoters[0].speed}</p>
                        </div>
                      </div>
                      
                      <div className="flex items-center gap-3 text-gray-300">
                        <span className="flex items-center justify-center w-10 h-10 rounded-lg bg-green-500/20">
                          <i className="fas fa-map-marker-alt text-green-500"></i>
                        </span>
                        <div>
                          <p className="text-sm text-gray-400">Район</p>
                          <p className="font-semibold">{topPromoters[0].area}</p>
                        </div>
                      </div>
                    </div>
                  </div>
                  
                  <div className="mt-6 flex flex-wrap gap-2 justify-center md:justify-start">
                    {topPromoters[0].achievements?.map((achievement, index) => (
                      <span 
                        key={index}
                        className={`px-4 py-2 rounded-full text-sm font-medium ${
                          index === 0 ? 'bg-primary/20 text-primary' :
                          index === 1 ? 'bg-secondary/20 text-secondary' :
                          'bg-gradient-to-r from-primary to-secondary text-white'
                        }`}
                      >
                        {achievement}
                      </span>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          ) : (
            <div className="text-center text-gray-400">
              Нет данных о промоутерах
            </div>
          )}
        </section>

        <section>
          <h2 className="text-3xl font-bold text-center mb-8 bg-gradient-to-r from-primary to-secondary text-transparent bg-clip-text">
            Текущий рейтинг промоутеров
          </h2>
          {loading ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {[1, 2, 3].map(i => (
                <LoadingCard key={i} />
              ))}
            </div>
          ) : error ? (
            <ErrorMessage />
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {topPromoters.map(promoter => (
                <div 
                  key={promoter.id} 
                  className="bg-dark/50 backdrop-blur-xl p-6 rounded-xl shadow-lg hover:shadow-neon transition-all transform hover:-translate-y-1"
                >
                  <div className="flex items-center gap-4">
                    <img 
                      src={promoter.avatar || "https://via.placeholder.com/60"} 
                      alt={promoter.name} 
                      className="w-14 h-14 rounded-full object-cover ring-2 ring-primary"
                    />
                    <div>
                      <h3 className="text-lg font-semibold text-white mb-2">{promoter.name}</h3>
                      <div className="flex flex-wrap gap-4">
                        <span className="flex items-center gap-2 text-gray-300 text-sm">
                          <i className="fas fa-file-alt"></i>
                          {promoter.flyers} листовок
                        </span>
                        <span className="flex items-center gap-2 text-gray-300 text-sm">
                          <i className="fas fa-star text-yellow-500"></i>
                          {promoter.rating} рейтинг
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </section>
      </main>
    </div>
  );
}

export default HomePage;
