import React from 'react';
import { motion } from 'framer-motion';
import { FaUser, FaLeaf } from 'react-icons/fa';

const PromoterCard = ({ promoter, rank, isTopPromoter = false }) => {
  // –ï—Å–ª–∏ –Ω–µ—Ç –∞–≤–∞—Ç–∞—Ä–∫–∏, –∏—Å–ø–æ–ª—å–∑—É–µ–º –∏–∫–æ–Ω–∫—É –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
  const avatarUrl = promoter.avatarUrl || null;
  
  // –û–ø—Ä–µ–¥–µ–ª—è–µ–º —Ü–≤–µ—Ç –¥–ª—è —Ä–∞–Ω–≥–∞
  const getRankColor = (rank) => {
    if (rank === 1) return 'text-yellow-400'; // –ó–æ–ª–æ—Ç–æ
    if (rank === 2) return 'text-gray-300'; // –°–µ—Ä–µ–±—Ä–æ
    if (rank === 3) return 'text-amber-600'; // –ë—Ä–æ–Ω–∑–∞
    return 'text-gray-500';
  };
  
  return (
    <motion.div
      whileHover={{ 
        y: -10, 
        transition: { type: 'spring', stiffness: 300 }
      }}
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.3, delay: rank * 0.1 }}
      className={`card ${isTopPromoter ? 'shadow-neon-purple' : 'card-hover'} relative`}
    >
      {/* –†–∞–Ω–≥ */}
      {rank && (
        <div className={`absolute -top-3 -left-3 w-8 h-8 rounded-full flex items-center justify-center font-bold ${getRankColor(rank)} bg-dark-darker border-2 border-current`}>
          {rank}
        </div>
      )}
      
      <div className="flex items-center">
        {/* –ê–≤–∞—Ç–∞—Ä–∫–∞ */}
        <div className="mr-4">
          {avatarUrl ? (
            <img 
              src={avatarUrl} 
              alt={`${promoter.name}`} 
              className="w-16 h-16 rounded-full object-cover border-2 border-primary"
            />
          ) : (
            <div className="w-16 h-16 rounded-full bg-dark-lighter flex items-center justify-center border-2 border-primary">
              <FaUser className="text-2xl text-gray-400" />
            </div>
          )}
        </div>
        
        {/* –ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è */}
        <div className="flex-1">
          <h3 className={`text-lg font-bold ${isTopPromoter ? 'text-neon-purple' : 'text-white'}`}>
            {promoter.name}
          </h3>
          
          <div className="flex items-center mt-1">
            <FaLeaf className="text-green-500 mr-2" />
            <span className="text-gray-300">
              {promoter.leafletsCount} –ª–∏—Å—Ç–æ–≤–æ–∫
            </span>
          </div>
        </div>
      </div>
      
      {/* –ù–∞–≥—Ä–∞–¥–∞ –¥–ª—è –ª—É—á—à–µ–≥–æ –ø—Ä–æ–º–æ—É—Ç–µ—Ä–∞ */}
      {isTopPromoter && promoter.reward && (
        <div className="mt-3 pt-3 border-t border-gray-700">
          <p className="text-yellow-400 font-medium">
            üèÜ –ù–∞–≥—Ä–∞–¥–∞: {promoter.reward}
          </p>
        </div>
      )}
    </motion.div>
  );
};

export default PromoterCard;
