import React, { useState, useEffect } from 'react';
import { FaUser, FaLeaf, FaUpload } from 'react-icons/fa';

const PromoterForm = ({ promoter, onSubmit, onCancel, isLoading }) => {
  const [name, setName] = useState('');
  const [leafletsCount, setLeafletsCount] = useState(0);
  const [avatarFile, setAvatarFile] = useState(null);
  const [avatarPreview, setAvatarPreview] = useState('');
  const [errors, setErrors] = useState({});
  
  // Инициализация формы при редактировании
  useEffect(() => {
    if (promoter) {
      setName(promoter.name || '');
      setLeafletsCount(promoter.leafletsCount || 0);
      setAvatarPreview(promoter.avatarUrl || '');
    }
  }, [promoter]);
  
  // Обработка выбора файла аватарки
  const handleAvatarChange = (e) => {
    const file = e.target.files[0];
    
    if (file) {
      setAvatarFile(file);
      
      // Создаем превью
      const reader = new FileReader();
      reader.onload = () => {
        setAvatarPreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };
  
  // Валидация формы
  const validateForm = () => {
    const newErrors = {};
    
    if (!name.trim()) {
      newErrors.name = 'Имя обязательно';
    }
    
    if (leafletsCount < 0) {
      newErrors.leafletsCount = 'Количество листовок не может быть отрицательным';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };
  
  // Отправка формы
  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (validateForm()) {
      onSubmit({
        name,
        leafletsCount: Number(leafletsCount)
      }, avatarFile);
    }
  };
  
  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      {/* Аватарка */}
      <div className="flex flex-col items-center mb-6">
        <div className="relative mb-4">
          {avatarPreview ? (
            <img 
              src={avatarPreview} 
              alt="Аватар" 
              className="w-24 h-24 rounded-full object-cover border-2 border-primary"
            />
          ) : (
            <div className="w-24 h-24 rounded-full bg-dark-lighter flex items-center justify-center border-2 border-primary">
              <FaUser className="text-3xl text-gray-400" />
            </div>
          )}
          
          <label 
            htmlFor="avatar-upload" 
            className="absolute -bottom-2 -right-2 bg-primary hover:bg-primary-dark text-white p-2 rounded-full cursor-pointer transition-colors"
            title="Загрузить аватар"
          >
            <FaUpload size={14} />
          </label>
          
          <input 
            id="avatar-upload"
            type="file"
            accept="image/*"
            onChange={handleAvatarChange}
            className="hidden"
            disabled={isLoading}
          />
        </div>
        
        <p className="text-sm text-gray-400">
          Нажмите на иконку для загрузки аватара
        </p>
      </div>
      
      {/* Имя */}
      <div>
        <label className="block text-gray-300 mb-2" htmlFor="name">
          Имя промоутера
        </label>
        <div className="relative">
          <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
            <FaUser className="text-gray-500" />
          </div>
          <input
            id="name"
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className={`input pl-10 w-full ${errors.name ? 'border-red-500' : ''}`}
            placeholder="Введите имя"
            disabled={isLoading}
          />
        </div>
        {errors.name && (
          <p className="mt-1 text-red-500 text-sm">{errors.name}</p>
        )}
      </div>
      
      {/* Количество листовок */}
      <div>
        <label className="block text-gray-300 mb-2" htmlFor="leafletsCount">
          Количество листовок
        </label>
        <div className="relative">
          <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
            <FaLeaf className="text-gray-500" />
          </div>
          <input
            id="leafletsCount"
            type="number"
            value={leafletsCount}
            onChange={(e) => setLeafletsCount(e.target.value)}
            className={`input pl-10 w-full ${errors.leafletsCount ? 'border-red-500' : ''}`}
            placeholder="0"
            min="0"
            disabled={isLoading}
          />
        </div>
        {errors.leafletsCount && (
          <p className="mt-1 text-red-500 text-sm">{errors.leafletsCount}</p>
        )}
      </div>
      
      {/* Кнопки */}
      <div className="flex justify-end space-x-4 pt-4">
        <button
          type="button"
          onClick={onCancel}
          className="btn bg-dark-lighter hover:bg-dark text-white"
          disabled={isLoading}
        >
          Отмена
        </button>
        <button
          type="submit"
          className="btn btn-primary"
          disabled={isLoading}
        >
          {isLoading ? 'Сохранение...' : (promoter ? 'Обновить' : 'Добавить')}
        </button>
      </div>
    </form>
  );
};

export default PromoterForm;
