                        <div className="absolute -top-5 left-1/2 -translate-x-1/2 w-10 h-10 bg-gradient-to-r from-gray-300 to-gray-400 rounded-full flex items-center justify-center text-dark font-bold text-lg shadow-lg animate-bounce" style={{ animationDuration: '2s' }}>
