                  <div className="absolute top-40 -left-20 w-72 h-72 bg-secondary/10 rounded-full blur-3xl"></div>
                  <div className="absolute top-40 -right-20 w-72 h-72 bg-primary/10 rounded-full blur-3xl"></div>
                </div>

