import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { FaLock, FaEnvelope } from 'react-icons/fa';
import { useAuth } from '../context/AuthContext';
import Header from '../components/Header';
import Footer from '../components/Footer';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showResetPassword, setShowResetPassword] = useState(false);
  
  const { login, resetPassword } = useAuth();
  const navigate = useNavigate();
  
  const handleLogin = async (e) => {
    e.preventDefault();
    
    if (!email || !password) {
      setError('Пожалуйста, заполните все поля');
      return;
    }
    
    try {
      setError('');
      setLoading(true);
      const success = await login(email, password);
      
      if (success) {
        navigate('/admin');
      }
    } catch (error) {
      console.error('Ошибка входа:', error);
      setError('Не удалось войти. Проверьте ваши учетные данные.');
    } finally {
      setLoading(false);
    }
  };
  
  const handleResetPassword = async (e) => {
    e.preventDefault();
    
    if (!email) {
      setError('Пожалуйста, введите email');
      return;
    }
    
    try {
      setError('');
      setLoading(true);
      await resetPassword(email);
      setShowResetPassword(false);
    } catch (error) {
      console.error('Ошибка сброса пароля:', error);
      setError('Не удалось отправить инструкции по сбросу пароля');
    } finally {
      setLoading(false);
    }
  };
  
  return (
    <div className="min-h-screen flex flex-col">
      <Header />
      
      <main className="flex-grow container mx-auto px-4 py-12 flex items-center justify-center">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          className="w-full max-w-md"
        >
          <div className="bg-dark rounded-xl shadow-lg p-8 border border-gray-800">
            <div className="text-center mb-8">
              <h2 className="text-2xl font-bold text-white">
                {showResetPassword ? 'Сброс пароля' : 'Вход для менеджеров'}
              </h2>
              <p className="text-gray-400 mt-2">
                {showResetPassword 
                  ? 'Введите ваш email для получения инструкций по сбросу пароля' 
                  : 'Войдите в систему для доступа к админ-панели'}
              </p>
            </div>
            
            {error && (
              <div className="bg-red-900/30 border border-red-800 text-red-300 px-4 py-3 rounded-md mb-6">
                {error}
              </div>
            )}
            
            {showResetPassword ? (
              <form onSubmit={handleResetPassword}>
                <div className="mb-6">
                  <label className="block text-gray-300 mb-2" htmlFor="email">
                    Email
                  </label>
                  <div className="relative">
                    <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                      <FaEnvelope className="text-gray-500" />
                    </div>
                    <input
                      id="email"
                      type="email"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      className="input pl-10 w-full"
                      placeholder="your@email.com"
                      disabled={loading}
                    />
                  </div>
                </div>
                
                <button
                  type="submit"
                  className="btn btn-primary w-full mb-4"
                  disabled={loading}
                >
                  {loading ? 'Отправка...' : 'Отправить инструкции'}
                </button>
                
                <div className="text-center">
                  <button
                    type="button"
                    onClick={() => setShowResetPassword(false)}
                    className="text-primary hover:text-primary-light text-sm"
                    disabled={loading}
                  >
                    Вернуться к форме входа
                  </button>
                </div>
              </form>
            ) : (
              <form onSubmit={handleLogin}>
                <div className="mb-6">
                  <label className="block text-gray-300 mb-2" htmlFor="email">
                    Email
                  </label>
                  <div className="relative">
                    <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                      <FaEnvelope className="text-gray-500" />
                    </div>
                    <input
                      id="email"
                      type="email"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      className="input pl-10 w-full"
                      placeholder="your@email.com"
                      disabled={loading}
                    />
                  </div>
                </div>
                
                <div className="mb-6">
                  <label className="block text-gray-300 mb-2" htmlFor="password">
                    Пароль
                  </label>
                  <div className="relative">
                    <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                      <FaLock className="text-gray-500" />
                    </div>
                    <input
                      id="password"
                      type="password"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      className="input pl-10 w-full"
                      placeholder="••••••••"
                      disabled={loading}
                    />
                  </div>
                </div>
                
                <button
                  type="submit"
                  className="btn btn-primary w-full mb-4"
                  disabled={loading}
                >
                  {loading ? 'Вход...' : 'Войти'}
                </button>
                
                <div className="text-center">
                  <button
                    type="button"
                    onClick={() => setShowResetPassword(true)}
                    className="text-primary hover:text-primary-light text-sm"
                    disabled={loading}
                  >
                    Забыли пароль?
                  </button>
                </div>
              </form>
            )}
          </div>
          
          <div className="text-center mt-6">
            <Link to="/" className="text-gray-400 hover:text-white">
              Вернуться на главную
            </Link>
          </div>
        </motion.div>
      </main>
      
      <Footer />
    </div>
  );
};

export default LoginPage;
