import React, { useState, useEffect } from 'react';
import { FaUser, FaLeaf, FaLink, FaCalendarAlt, FaMapMarkerAlt } from 'react-icons/fa';

const PromoterForm = ({ promoter, onSubmit, onCancel, isLoading }) => {
  const [name, setName] = useState('');
  const [leafletsCount, setLeafletsCount] = useState(0);
  const [workDays, setWorkDays] = useState(0);
  const [district, setDistrict] = useState('');
  const [avatarUrl, setAvatarUrl] = useState('');
  const [avatarPreview, setAvatarPreview] = useState('');
  const [errors, setErrors] = useState({});
  
  // Районы Петрозаводска
  const districts = [
    'Центр',
    'Древлянка',
    'Кукковка',
    'Ключевая',
    'Голиковка',
    'Зарека',
    'Перевалка',
    'Октябрьский',
    'Первомайский',
    'Сулажгора'
  ];
  
  // Инициализация формы при редактировании
  useEffect(() => {
    if (promoter) {
      setName(promoter.name || '');
      setLeafletsCount(promoter.leafletsCount || 0);
      setWorkDays(promoter.workDays || 0);
      setDistrict(promoter.district || '');
      setAvatarUrl(promoter.avatarUrl || '');
      setAvatarPreview(promoter.avatarUrl || '');
    }
  }, [promoter]);
  
  // Обработка изменения URL аватара
  const handleAvatarUrlChange = (e) => {
    const url = e.target.value;
    setAvatarUrl(url);
    setAvatarPreview(url);
  };
  
  // Валидация формы
  const validateForm = () => {
    const newErrors = {};
    
    if (!name.trim()) {
      newErrors.name = 'Имя обязательно';
    }
    
    if (leafletsCount < 0) {
      newErrors.leafletsCount = 'Количество листовок не может быть отрицательным';
    }

    if (workDays < 0) {
      newErrors.workDays = 'Количество дней работы не может быть отрицательным';
    }

    if (!district) {
      newErrors.district = 'Выберите район';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };
  
  // Отправка формы
  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (validateForm()) {
      onSubmit({
        name,
        leafletsCount: Number(leafletsCount),
        workDays: Number(workDays),
        district,
        avatarUrl,
        speed: workDays > 0 ? Math.round(leafletsCount / workDays) : 0
      }, null);
    }
  };
  
  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      {/* Аватарка */}
      <div className="flex flex-col items-center mb-6">
        <div className="relative mb-4">
          {avatarPreview ? (
            <img 
              src={avatarPreview} 
              alt="Аватар" 
              className="w-24 h-24 rounded-full object-cover border-2 border-primary"
              onError={(e) => {
                e.target.onerror = null;
                e.target.src = `https://ui-avatars.com/api/?name=${encodeURIComponent(name)}&background=random&color=fff&size=128`;
              }}
            />
          ) : (
            <div className="w-24 h-24 rounded-full bg-dark-lighter flex items-center justify-center border-2 border-primary">
              <FaUser className="text-3xl text-gray-400" />
            </div>
          )}
        </div>
        
        <p className="text-sm text-gray-400 mb-2">
          Введите URL изображения или оставьте пустым для автоматической генерации
        </p>
        
        <div className="w-full">
          <div className="relative">
            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
              <FaLink className="text-gray-500" />
            </div>
            <input
              type="text"
              value={avatarUrl}
              onChange={handleAvatarUrlChange}
              className="input pl-10 w-full"
              placeholder="https://example.com/avatar.jpg"
              disabled={isLoading}
            />
          </div>
        </div>
      </div>
      
      {/* Имя */}
      <div>
        <label className="block text-gray-300 mb-2" htmlFor="name">
          Имя промоутера
        </label>
        <div className="relative">
          <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
            <FaUser className="text-gray-500" />
          </div>
          <input
            id="name"
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className={`input pl-10 w-full ${errors.name ? 'border-red-500' : ''}`}
            placeholder="Введите имя"
            disabled={isLoading}
          />
        </div>
        {errors.name && (
          <p className="mt-1 text-red-500 text-sm">{errors.name}</p>
        )}
      </div>
      
      {/* Количество листовок */}
      <div>
        <label className="block text-gray-300 mb-2" htmlFor="leafletsCount">
          Количество листовок
        </label>
        <div className="relative">
          <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
            <FaLeaf className="text-gray-500" />
          </div>
          <input
            id="leafletsCount"
            type="number"
            value={leafletsCount}
            onChange={(e) => setLeafletsCount(e.target.value)}
            className={`input pl-10 w-full ${errors.leafletsCount ? 'border-red-500' : ''}`}
            placeholder="0"
            min="0"
            disabled={isLoading}
          />
        </div>
        {errors.leafletsCount && (
          <p className="mt-1 text-red-500 text-sm">{errors.leafletsCount}</p>
        )}
      </div>

      {/* Дни работы */}
      <div>
        <label className="block text-gray-300 mb-2" htmlFor="workDays">
          Количество дней работы
        </label>
        <div className="relative">
          <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
            <FaCalendarAlt className="text-gray-500" />
          </div>
          <input
            id="workDays"
            type="number"
            value={workDays}
            onChange={(e) => setWorkDays(e.target.value)}
            className={`input pl-10 w-full ${errors.workDays ? 'border-red-500' : ''}`}
            placeholder="0"
            min="0"
            disabled={isLoading}
          />
        </div>
        {errors.workDays && (
          <p className="mt-1 text-red-500 text-sm">{errors.workDays}</p>
        )}
      </div>

      {/* Район */}
      <div>
        <label className="block text-gray-300 mb-2" htmlFor="district">
          Район города
        </label>
        <div className="relative">
          <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
            <FaMapMarkerAlt className="text-gray-500" />
          </div>
          <select
            id="district"
            value={district}
            onChange={(e) => setDistrict(e.target.value)}
            className={`input pl-10 w-full ${errors.district ? 'border-red-500' : ''}`}
            disabled={isLoading}
          >
            <option value="">Выберите район</option>
            {districts.map((d) => (
              <option key={d} value={d}>{d}</option>
            ))}
          </select>
        </div>
        {errors.district && (
          <p className="mt-1 text-red-500 text-sm">{errors.district}</p>
        )}
      </div>
      
      {/* Кнопки */}
      <div className="flex justify-end space-x-4 pt-4">
        <button
          type="button"
          onClick={onCancel}
          className="btn bg-dark-lighter hover:bg-dark text-white"
          disabled={isLoading}
        >
          Отмена
        </button>
        <button
          type="submit"
          className="btn btn-primary"
          disabled={isLoading}
        >
          {isLoading ? 'Сохранение...' : (promoter ? 'Обновить' : 'Добавить')}
        </button>
      </div>
    </form>
  );
};

export default PromoterForm;
