import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { FaTelegram, FaKey, FaHashtag, FaToggleOn, FaToggleOff, FaPaperPlane } from 'react-icons/fa';
import { toast } from 'react-toastify';
import { getTelegramConfig, updateTelegramConfig, sendTelegramMessage } from '../../firebase/telegramService';

const AdminTelegram = () => {
  const [config, setConfig] = useState({
    botToken: '',
    channelId: '',
    enabled: false
  });
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [testMessage, setTestMessage] = useState('');
  const [sendingTest, setSendingTest] = useState(false);
  
  // Загрузка данных
  useEffect(() => {
    fetchConfig();
  }, []);
  
  // Получение конфигурации Telegram
  const fetchConfig = async () => {
    try {
      setLoading(true);
      const data = await getTelegramConfig();
      setConfig(data);
    } catch (error) {
      console.error('Ошибка при загрузке конфигурации Telegram:', error);
      toast.error('Не удалось загрузить настройки Telegram');
    } finally {
      setLoading(false);
    }
  };
  
  // Сохранение конфигурации
  const handleSaveConfig = async (e) => {
    e.preventDefault();
    
    try {
      setSaving(true);
      await updateTelegramConfig(config);
      toast.success('Настройки Telegram успешно сохранены');
    } catch (error) {
      console.error('Ошибка при сохранении настроек Telegram:', error);
      toast.error('Не удалось сохранить настройки Telegram');
    } finally {
      setSaving(false);
    }
  };
  
  // Отправка тестового сообщения
  const handleSendTestMessage = async (e) => {
    e.preventDefault();
    
    if (!testMessage.trim()) {
      toast.warning('Введите текст сообщения');
      return;
    }
    
    try {
      setSendingTest(true);
      const success = await sendTelegramMessage(testMessage);
      
      if (success) {
        toast.success('Тестовое сообщение успешно отправлено');
        setTestMessage('');
      } else {
        toast.warning('Не удалось отправить сообщение. Проверьте настройки Telegram');
      }
    } catch (error) {
      console.error('Ошибка при отправке тестового сообщения:', error);
      toast.error('Ошибка при отправке тестового сообщения');
    } finally {
      setSendingTest(false);
    }
  };
  
  // Переключение статуса интеграции
  const toggleEnabled = () => {
    setConfig({
      ...config,
      enabled: !config.enabled
    });
  };
  
  if (loading) {
    return (
      <div className="flex-1 p-6 flex items-center justify-center">
        <div className="animate-pulse text-center">
          <div className="h-8 bg-dark-lighter rounded w-64 mx-auto mb-4"></div>
          <div className="h-4 bg-dark-lighter rounded w-48 mx-auto"></div>
        </div>
      </div>
    );
  }
  
  return (
    <div className="flex-1 p-6 overflow-auto">
      <div className="max-w-6xl mx-auto">
        {/* Заголовок */}
        <motion.h2
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          className="text-2xl font-bold text-white mb-6"
        >
          Интеграция с Telegram
        </motion.h2>
        
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* Форма настроек */}
          <motion.div
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5 }}
            className="bg-dark rounded-xl p-6"
          >
            <h3 className="text-xl font-bold text-white mb-4 flex items-center">
              <FaTelegram className="text-blue-400 mr-2" />
              Настройки Telegram
            </h3>
            
            <form onSubmit={handleSaveConfig} className="space-y-6">
              {/* Токен бота */}
              <div>
                <label className="block text-gray-300 mb-2" htmlFor="botToken">
                  Токен бота
                </label>
                <div className="relative">
                  <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                    <FaKey className="text-gray-500" />
                  </div>
                  <input
                    id="botToken"
                    type="text"
                    value={config.botToken}
                    onChange={(e) => setConfig({ ...config, botToken: e.target.value })}
                    className="input pl-10 w-full"
                    placeholder="Введите токен бота"
                    disabled={saving}
                  />
                </div>
                <p className="mt-1 text-sm text-gray-400">
                  Получите токен бота через @BotFather в Telegram
                </p>
              </div>
              
              {/* ID канала */}
              <div>
                <label className="block text-gray-300 mb-2" htmlFor="channelId">
                  ID канала
                </label>
                <div className="relative">
                  <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                    <FaHashtag className="text-gray-500" />
                  </div>
                  <input
                    id="channelId"
                    type="text"
                    value={config.channelId}
                    onChange={(e) => setConfig({ ...config, channelId: e.target.value })}
                    className="input pl-10 w-full"
                    placeholder="Например: @your_channel или -1001234567890"
                    disabled={saving}
                  />
                </div>
                <p className="mt-1 text-sm text-gray-400">
                  Укажите @username канала или его числовой ID
                </p>
              </div>
              
              {/* Включение/выключение */}
              <div>
                <label className="flex items-center cursor-pointer">
                  <div className="mr-3">
                    {config.enabled ? (
                      <FaToggleOn className="text-blue-400 text-2xl" />
                    ) : (
                      <FaToggleOff className="text-gray-500 text-2xl" />
                    )}
                  </div>
                  <span className="text-gray-300">
                    {config.enabled ? 'Интеграция включена' : 'Интеграция отключена'}
                  </span>
                  <input
                    type="checkbox"
                    className="hidden"
                    checked={config.enabled}
                    onChange={toggleEnabled}
                    disabled={saving}
                  />
                </label>
              </div>
              
              {/* Кнопка сохранения */}
              <div className="pt-4">
                <button
                  type="submit"
                  className="btn btn-primary w-full"
                  disabled={saving}
                >
                  {saving ? 'Сохранение...' : 'Сохранить настройки'}
                </button>
              </div>
            </form>
          </motion.div>
          
          {/* Тестовое сообщение и инструкции */}
          <motion.div
            initial={{ opacity: 0, x: 20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5 }}
            className="space-y-6"
          >
            {/* Тестовое сообщение */}
            <div className="bg-dark rounded-xl p-6">
              <h3 className="text-xl font-bold text-white mb-4 flex items-center">
                <FaPaperPlane className="text-blue-400 mr-2" />
                Тестовое сообщение
              </h3>
              
              <form onSubmit={handleSendTestMessage} className="space-y-4">
                <div>
                  <textarea
                    value={testMessage}
                    onChange={(e) => setTestMessage(e.target.value)}
                    className="input w-full h-32"
                    placeholder="Введите текст сообщения для отправки в Telegram..."
                    disabled={sendingTest || !config.enabled}
                  ></textarea>
                </div>
                
                <button
                  type="submit"
                  className="btn bg-blue-500 hover:bg-blue-600 text-white w-full flex items-center justify-center"
                  disabled={sendingTest || !config.enabled}
                >
                  <FaTelegram className="mr-2" />
                  {sendingTest ? 'Отправка...' : 'Отправить тестовое сообщение'}
                </button>
                
                {!config.enabled && (
                  <p className="text-yellow-500 text-sm text-center">
                    Для отправки сообщений необходимо включить интеграцию
                  </p>
                )}
              </form>
            </div>
            
            {/* Инструкции */}
            <div className="bg-dark rounded-xl p-6">
              <h3 className="text-lg font-bold text-white mb-4">
                Инструкция по настройке
              </h3>
              
              <ol className="space-y-2 text-gray-400 list-decimal list-inside">
                <li>Создайте нового бота через @BotFather в Telegram</li>
                <li>Получите токен бота и вставьте его в поле "Токен бота"</li>
                <li>Создайте канал в Telegram или используйте существующий</li>
                <li>Добавьте вашего бота в канал как администратора</li>
                <li>Укажите @username канала или его числовой ID в поле "ID канала"</li>
                <li>Включите интеграцию, переключив соответствующий тумблер</li>
                <li>Сохраните настройки и отправьте тестовое сообщение для проверки</li>
              </ol>
              
              <div className="mt-4 p-3 bg-blue-900/30 border border-blue-800 rounded-md">
                <p className="text-blue-300 text-sm">
                  <strong>Примечание:</strong> Для работы с Telegram Bot API в продакшене рекомендуется 
                  использовать серверную часть (Cloud Functions), чтобы не хранить токен бота на клиенте.
                </p>
              </div>
            </div>
          </motion.div>
        </div>
      </div>
    </div>
  );
};

export default AdminTelegram;
